CREATE PROCEDURE [dbo].[spSearchAccountLedgers]
	@SearchBy Varchar(100),
	@LedgerTypeID INT,
	@CompanyID INT,
	@TitleLike Varchar(500)=''
AS
	/* SET NOCOUNT ON */
	
if 	@SearchBy='Account and Sub Ledger'
SELECT  T1.AccountID, 
		T1.AccountNo, 
		T1.AccountTitle, 
		T1.AccOrGroup, 
        T1.AccCreateDate, 
        T1.AccDepth, 
        T1.Nature, 
        CASE WHEN T1.nature = 1 THEN 'Debit' ELSE 'Credit' END AS AccNature,
        T1.AccountStatus, 
        T1.ParentID, 
        T2.AccountTitle AS Parent,
        T1.OpeningBalance, 
        T1.CurrentBalance, 
        T1.IsInventoryRelated, 
        CASE WHEN T1.IsInventoryRelated = 1 THEN 'YES' ELSE 'NO' END AS IsInvt,
        T1.LedgerID,
        T1.CompanyID, 
        T1.UserID, 
        T1.ModifiedDate 
        
FROM  T_Account AS T1 INNER JOIN T_Account AS T2 ON T1.ParentID = T2.AccountID
WHERE T1.CompanyID=@CompanyID
ORDER BY T1.AccountNo    

if   @SearchBy='Account Only'

SELECT  T1.AccountID, 
		T1.AccountNo, 
		T1.AccountTitle, 
		T1.AccOrGroup, 
        T1.AccCreateDate, 
        T1.AccDepth, 
        T1.Nature, 
        CASE WHEN T1.nature = 1 THEN 'Debit' ELSE 'Credit' END AS AccNature,
        T1.AccountStatus, 
        T1.ParentID, 
        T2.AccountTitle AS Parent,
        T1.OpeningBalance, 
        T1.CurrentBalance, 
        T1.IsInventoryRelated, 
        CASE WHEN T1.IsInventoryRelated = 1 THEN 'YES' ELSE 'NO' END AS IsInvt,
        T1.LedgerID,
        T1.CompanyID, 
        T1.UserID, 
        T1.ModifiedDate 
FROM         T_Account AS T1 INNER JOIN
                      T_Account AS T2 ON T1.ParentID = T2.AccountID
WHERE  T1.CompanyID=@CompanyID AND (T1.AccOrGroup LIKE 'Account')
ORDER BY T1.AccountNo    

if @SearchBy='Sub Ledger'
SELECT  T1.AccountID, 
		T1.AccountNo, 
		T1.AccountTitle, 
		T1.AccOrGroup, 
        T1.AccCreateDate, 
        T1.AccDepth, 
        T1.Nature, 
        CASE WHEN T1.nature = 1 THEN 'Debit' ELSE 'Credit' END AS AccNature,
        T1.AccountStatus, 
        T1.ParentID, 
        T2.AccountTitle AS Parent,
        T1.OpeningBalance, 
        T1.CurrentBalance, 
        T1.IsInventoryRelated, 
        CASE WHEN T1.IsInventoryRelated = 1 THEN 'YES' ELSE 'NO' END AS IsInvt,
        T1.LedgerID,
        T1.CompanyID, 
        T1.UserID, 
        T1.ModifiedDate 
FROM         T_Account AS T1 INNER JOIN
                      T_Account AS T2 ON T1.ParentID = T2.AccountID
WHERE  T1.CompanyID=@CompanyID AND    (T1.AccOrGroup Not LIKE 'Account')
ORDER BY T1.AccountNo      


if 	@SearchBy='Account'
SELECT  T1.AccountID, 
		T1.AccountNo, 
		T1.AccountTitle, 
		--T1.AccOrGroup, 
        --T1.AccCreateDate, 
        --T1.AccDepth, 
        --T1.Nature, 
        --CASE WHEN T1.nature = 1 THEN 'Debit' ELSE 'Credit' END AS AccNature,
        --T1.AccountStatus, 
        T1.ParentID, 
        T2.AccountTitle AS Parent,
        T1.OpeningBalance, 
        T1.CurrentBalance, 
        --T1.IsInventoryRelated, 
       -- CASE WHEN T1.IsInventoryRelated = 1 THEN 'YES' ELSE 'NO' END AS IsInvt,
        T1.LedgerID
        --T1.CompanyID, 
        --T1.UserID, 
        --T1.ModifiedDate 
        
FROM  T_Account AS T1 Left outer JOIN T_Account AS T2 ON T1.ParentID = T2.AccountID
--WHERE T1.LedgerTypeID=@LedgerTypeID or T1.LedgerTypeID is null
WHERE T1.CompanyID=@CompanyID /*AND(T1.LedgerTypeID = @LedgerTypeID)*/ AND T1.AccountTitle LIKE @TitleLike+'%'
ORDER BY T1.AccountNo          

if 	@SearchBy='Ledger'
SELECT  T_Ledgers.LedgerID, 
		T_Ledgers.LedgerName, 
		T_Ledgers.Address, 
		--T_Ledgers.CountryID, 
		--T_Ledgers.CurrencyID, 
		T_Ledgers.ContactPerson, 
        T_Ledgers.bankAccType as [A/C Type], 
        T_Ledgers.BusinessType as [Business Type], 
        T_Ledgers.Phone, 
        T_Ledgers.Fax, 
        T_Ledgers.Email, 
        T_Ledgers.AccountID, 
        T_Ledgers.Remarks, 
        --T_Team_Master.TeamID , 
        --T_Team_Master.TeamNo, 
        T_Team_Master.TeamName, 
       -- T_Ledgers.TeamID AS memberID, 
        T_Team_Detail.MemberName
FROM         T_Team_Master INNER JOIN
                      T_Team_Detail ON T_Team_Master.TeamID = T_Team_Detail.TeamID RIGHT OUTER JOIN
                      T_Ledgers ON T_Team_Detail.MemberID = T_Ledgers.TeamID

WHERE  T_Ledgers.CompanyID=@CompanyID AND(T_Ledgers.LedgerTypeID = @LedgerTypeID)  AND  T_Ledgers.LedgerName LIKE @TitleLike+'%'
ORDER BY  LedgerName      
	RETURN






