CREATE PROCEDURE [dbo].[spSearchEmployee]
	@CardNo varchar(100),
	@Name varchar(100),
	@ProximityNo varchar(100),
	@EmpCategoryID int,
	@DesignationID int,
	@SectionID int,
	@FloorID int,
	@LineID int,
	@Address varchar(800),
	@Gender varchar(20),
	@BloodGroup varchar(10),
	@Religion varchar(20),
	@NationalID varchar(100),
	@JDstart datetime,
	@JDend datetime,
	@BDstart datetime,
	@BDend datetime
AS
	
SELECT  EmployeeID, CardNo, Name, ProximityNo, JoiningDate, EmpCategoryID, DesignationID, SectionID, FloorID, LineID, Address, Gender, BloodGroup, 
        Religion, DateOfBirth, Photograph, NationalID
FROM  
vw_Employees
WHERE     
(CardNo LIKE @CardNo OR @CardNo LIKE 'ALL') AND 
(Name LIKE @Name OR  @Name LIKE 'ALL') AND 
(ProximityNo LIKE @ProximityNo OR @ProximityNo LIKE 'ALL') AND 
(EmpCategoryID = @EmpCategoryID OR @EmpCategoryID = 0) AND 
(DesignationID = @DesignationID OR @DesignationID = 0) AND 
(SectionID = @SectionID OR @SectionID = 0) AND
(FloorID = @FloorID OR @FloorID = 0) AND 
(LineID = @LineID OR @LineID = 0) AND 
(Address LIKE @Address OR @Address LIKE 'ALL') AND 
(Gender LIKE @Gender OR @Gender LIKE 'ALL') AND 
(BloodGroup LIKE @BloodGroup OR @BloodGroup LIKE 'ALL') AND 
(Religion LIKE @Religion OR @Religion LIKE 'ALL') AND 
(NationalID LIKE @NationalID OR @NationalID LIKE 'ALL') AND 
((JoiningDate between @JDstart and @JDend) OR (@JDstart IS NULL)) AND
((DateOfBirth between @BDstart and @BDend) OR (@BDstart IS NULL))
	RETURN






