CREATE PROCEDURE [dbo].[spSelectBillPaymentsForLCAdjust]
@SupplierAccID INT,
@isLCPay INT,
@isAll INT	
AS
BEGIN
SELECT     BA.BillAdjustID, BA.BillsID, BA.AdjustDate AS PayDate, BA.AdjustAmount AS PayAmtTK, BA.AdjustAmount / (CASE WHEN ISNULL(BA.CurrencyRate,0)=0 THEN 1 ELSE BA.CurrencyRate END) AS PayAmtFC, 
                      A2.AccountTitle AS PaidFrom, BA.AdjustRefLCID, LC.LCNo AS PayFromLC, A1.AccountTitle AS BillOf, BA.CurrencyRate, BA.CurrencyID, 
                      BA.AdjustAmount - ISNULL(LCA.AdjustAmount, 0) AS DueAmt
FROM         T_Bills_Adjust AS BA LEFT OUTER JOIN
                          (SELECT     BillPayID, LCID, SUM(AdjustAmount) AS AdjustAmount
                            FROM          T_Import_BillLCAdjust
                            GROUP BY BillPayID, LCID) AS LCA ON BA.BillAdjustID = LCA.BillPayID LEFT OUTER JOIN
                      T_LC_Master AS LC ON BA.AdjustRefLCID = LC.LCID LEFT OUTER JOIN
                      T_Account AS A2 ON BA.AdjustAccID = A2.AccountID LEFT OUTER JOIN
                      T_Account AS A1 ON BA.BillAccID = A1.AccountID
WHERE   BA.BillsID IN (SELECT BillsID FROM T_Bills WHERE BillType LIKE 'Bills Payable')
		AND (ISNULL(BA.BillAccID,0) =@SupplierAccID OR @SupplierAccID <=0) 
		AND ( (@isLCPay=1 AND BA.AdjustRefLCID IS NOT NULL) OR @isLCPay=0)
AND ( @isAll=1 OR (@isAll=0 AND BA.AdjustAmount <> ISNULL(LCA.AdjustAmount, 0)))
ORDER BY PayDate

END





