CREATE PROCEDURE [dbo].[spSelectEmployeeForSalarySheet2]
	@startDate DATETIME,
	@endDate DATETIME,
	@CardNo VARCHAR(50),
	@DesignationID INT,
	@SectionID INT,
	@FloorID INT,
	@LineID INT,
	@CompanyID INT
AS
	 --SET NOCOUNT ON 
SELECT 
        X.EmployeeID, 
		X.CardNo,
		X.Name, 
		X.JoiningDate, 
		X.CompanyID, 
        X.Designation, 
        X.Section,  
        X.Floor, 
        X.Line,
        Y.present,
        Y.Latepresent,
        Y.Lunchpresent,
        Y.Leave,
        Y.Holiday,
        Y.holidayPresent,
        Y.OTminute,
        R.OTRate,
        S.BasicSalary,
        S.GrossSalary,
        S.HouseRent,
        S.Medical,
        S.Conveyance,
       ISNULL(S.AdvanceDeduction,0) AS AdvanceDeduction
       
FROM 
( SELECT  Employee.EmployeeID, 
				Employee.CardNo, 
				Employee.Name, 
				Employee.JoiningDate, 
				Employee.CompanyID, 
                Designation.Name AS Designation, 
                [Section].Name AS [Section],  
                [Floor].Name AS [Floor], 
                Line.Name AS Line
         FROM   Employee INNER JOIN
             EmployeeHistory ON Employee.EmployeeHistoryID = EmployeeHistory.EmployeeHistoryID INNER JOIN
             Section ON EmployeeHistory.SectionID = Section.SectionID INNER JOIN
             Floor ON EmployeeHistory.FloorID = Floor.FloorID INNER JOIN
             Line ON EmployeeHistory.LineID = Line.LineID INNER JOIN
             Designation ON EmployeeHistory.DesignationID = Designation.DesignationID
        WHERE (Employee.CardNo = @CardNo OR @CardNo='all') 
			AND (Employee.CompanyID = @CompanyID) 
			AND (EmployeeHistory.DesignationID = @DesignationID OR @DesignationID=0) 
			AND (EmployeeHistory.SectionID = @SectionID OR @SectionID=0) 
			AND (EmployeeHistory.FloorID = @FloorID OR @FloorID=0) 
			AND (EmployeeHistory.LineID = @LineID OR @LineID=0)
	) X	,
		
		
	( SELECT  employeeID, 
			  SUM((CASE WHEN type = 'Present' THEN 1 ELSE 0 END)) AS present, 
              SUM((CASE WHEN type = 'Late Present' THEN 1 ELSE 0 END)) AS Latepresent, 
              SUM((CASE WHEN type = 'Lunch Absent' THEN 1 ELSE 0 END)) AS Lunchpresent, 
              SUM((CASE WHEN type = 'Leave' THEN 1 ELSE 0 END)) AS Leave, 
              SUM((CASE WHEN type = 'Holiday' THEN 1 ELSE 0  END)) AS Holiday, 
              SUM((CASE WHEN type = 'Holiday Present' THEN 1 ELSE 0 END)) AS holidayPresent, 
              sum((CASE WHEN OT <=0 THEN 0 ELSE OT END)) AS OTminute,
              companyID
	 FROM    vw_AttInOut
	 WHERE   (attDate BETWEEN @startDate AND @endDate) AND (CompanyID = @CompanyID)
	 GROUP BY employeeID, CompanyID
   ) Y,
   
   ( SELECT EmployeeSalary.*, Adv.AdvanceDeduction FROM EmployeeSalary LEFT OUTER JOIN
   ( SELECT  Advance.EmployeeID, 
		   --ISNULL(PaidAdvance.PaidAmount, 0) AS PaidAmount, 
		   --Advance.SanctionAmount, 
           --Advance.SanctionAmount - ISNULL(PaidAdvance.PaidAmount, 0) AS due, 
           --Advance.ReturnAmont, 
           (CASE WHEN Advance.ReturnAmount >= (Advance.SanctionAmount - ISNULL(PaidAdvance.PaidAmount, 0)) /* DUE */  THEN Advance.SanctionAmount - ISNULL(PaidAdvance.PaidAmount, 0) /* DUE */ ELSE Advance.ReturnAmount END) AS AdvanceDeduction
     FROM     
      (SELECT  AdvanceID, SUM(PaidAmount) AS PaidAmount FROM AdvancePayment WHERE (PaidDate <= @startDate) GROUP BY AdvanceID) AS PaidAdvance 
       RIGHT OUTER JOIN  Advance 
     ON PaidAdvance.AdvanceID = Advance.AdvanceID
     WHERE (Advance.SanctionAmount - ISNULL(PaidAdvance.PaidAmount, 0) /* DUE */  > 0)
    ) Adv ON EmployeeSalary.EmployeeID= Adv.EmployeeID
   ) S,
   EmployeeOTrule R
   

 WHERE X.employeeID=Y.employeeID AND
      X.employeeID=S.employeeID AND
      X.employeeID=R.employeeID 
     
	RETURN






GO

