AS
BEGIN

IF ISNULL(@StockMID,0) >0 
SELECT     T_Stock_InOut_Master.RefID AS OrderID, T_Stock_InOut_Detail.StockDID, T_Stock_InOut_Detail.StockMID, T_Stock_InOut_Detail.TransNature, 
                      T_Stock_InOut_Detail.ItemID, T_Stock_InOut_Detail.CountID, T_Stock_InOut_Detail.SizeID, T_Stock_InOut_Detail.ColorID, 
                      T_Stock_InOut_Detail.ColorCode, T_Stock_InOut_Detail.Labdip, dbo.fnOrderQty(T_Stock_InOut_Master.RefID, T_Stock_InOut_Detail.ItemID, 
                      T_Stock_InOut_Detail.CountID, T_Stock_InOut_Detail.SizeID, T_Stock_InOut_Detail.ColorID, T_Stock_InOut_Detail.ColorCode, 
                      T_Stock_InOut_Detail.Labdip, T_Stock_InOut_Master.CompanyID) AS Qty, T_Stock_InOut_Detail.InQty + T_Stock_InOut_Detail.OutQty AS AvailQty, 
                      P_Units.UnitsName AS Unit, T_Stock_InOut_Detail.UnitPrice, T_Stock_InOut_Detail.InAmount + T_Stock_InOut_Detail.OutAmount AS Amount, 
                      T_Stock_InOut_Detail.Remarks, isnull(T_Stock_InOut_Master.CustSupplID,0) as LedgerID
FROM         T_Stock_InOut_Master INNER JOIN
                      T_Stock_InOut_Detail ON T_Stock_InOut_Master.StockMID = T_Stock_InOut_Detail.StockMID INNER JOIN
                      T_Item ON T_Stock_InOut_Detail.ItemID = T_Item.ItemID INNER JOIN
                      P_Units ON T_Item.UnitID = P_Units.UnitsID LEFT OUTER JOIN
                      P_Colors ON T_Stock_InOut_Detail.ColorID = P_Colors.ColorsID LEFT OUTER JOIN
                      P_Sizes ON T_Stock_InOut_Detail.SizeID = P_Sizes.SizesID LEFT OUTER JOIN
                      T_Count ON T_Stock_InOut_Detail.CountID = T_Count.CountID
WHERE     (T_Stock_InOut_Detail.StockMID = @StockMID)
ORDER BY T_Item.ItemName, T_Count.CountName, P_Sizes.SizesName, P_Colors.ColorsName

ELSE
Declare @CompanyID INT ;
SET @CompanyID= (SELECT CompanyID FROM Order_Master WHERE OrderMID=@OrderMID);
SELECT     Order_Details.OrderMID AS OrderID, 0 AS StockDID, 0 AS StockMID, @TransNature AS TransNature, Order_Details.ItemID, Order_Details.CountID, 
