WHERE     (T_Stock_InOut_Master.TransType LIKE '%Customer') AND (T_Stock_InOut_Detail.InQty+T_Stock_InOut_Detail.OutQty>0) 
					AND (T_Ledgers.LedgerID = @LedgerID OR
