CREATE PROCEDURE [dbo].[spTEST]
	 @startDate DateTime,
	 @endDate DateTime,
	 @employeeID int
AS

Declare @MaxOT as INT, @CardNo AS VARCHAR(100), @CompanyID AS INT


SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1),0)

exec spGenerateDummyDate @startDate,@endDate;

SET @CompanyID = 1

 IF @MaxOT > 0  /* Buyer*/ 
 BEGIN

	SELECT     @employeeID AS employeeID, D.dt AS attDate, Max(A.entryTime) AS EntryTime, Case WHEN A.OT > @MaxOT*60 THEN  DateAdd(mi, -(A.OT -@MaxOT*60), MAX(A.exitTime) ) Else Max(A.exitTime) END  AS exitTime, MIN(ISNULL(A.Type, 'Absent')) AS type, A.OT
	FROM         DummyDate AS D LEFT OUTER JOIN
							  (SELECT     EmployeeID, attDate, entryTime, exitTime, (CASE WHEN Remarks LIKE '1' AND type LIKE 'Holiday%'  THEN 'Weekly ' WHEN Remarks LIKE '0' AND type LIKE 'Holiday%' THEN 'National ' ELSE '' END) + type AS Type, OT
								FROM          fnvw_AttInOut(@startDate, @endDate, @CompanyID) AS VW_InOut
								WHERE (EmployeeID = @employeeID) AND (attDate BETWEEN @startDate AND @endDate) AND vw_AttInOut.Type NOT LIKE 'Holiday Present') AS A ON D.dt = A.attDate
	GROUP BY A.EmployeeID, D.dt, A.OT
	ORDER BY attDate
END
ELSE
BEGIN
		
	SELECT     @employeeID AS employeeID, D.dt AS attDate, @MaxOT, MAX(A.entryTime) AS entryTime, MAX(A.exitTime) AS exitTime, MIN(ISNULL(A.Type, 'Absent')) AS type, Max(A.OT) AS OT
	FROM         DummyDate AS D LEFT OUTER JOIN
							  (SELECT     EmployeeID, attDate, entryTime, exitTime, (CASE WHEN Remarks LIKE '1' AND type LIKE 'Holiday%'  THEN 'Weekly ' WHEN Remarks LIKE '0' AND type LIKE 'Holiday%' THEN 'National ' ELSE '' END) + type AS Type, OT
								FROM          fnvw_AttInOut(@startDate, @endDate, @CompanyID) AS VW_InOut
								WHERE      (EmployeeID = @employeeID) AND (attDate BETWEEN @startDate AND @endDate)) AS A ON D.dt = A.attDate
	GROUP BY A.EmployeeID, D.dt
	ORDER BY attDate
END

	RETURN





