CREATE PROCEDURE [dbo].[T_spCloseFiscalYear]
@cost_of_goods_sold Money,
@rowMaterials Money,
@finishedGoods Money,
@WorkProgress Money,
@dividend Money,
@newFYTitle Varchar(500),
@date DateTime,
@UserID INT,
@CompanyID INT

AS
BEGIN
Declare @plBalance Money
Declare @PrevFYID INT;

SET  @PrevFYID=ISNULL((SELECT FiscalYearID FROM FiscalYear WHERE (enddate IS NULL) AND (companyID = @companyID)),0)

IF @PrevFYID > 0
BEGIN 
---Calculate Profit or Loss
SET @plBalance = (SELECT dbo.fnGetProfitOrLoss(@cost_of_goods_sold,@dividend,@CompanyID)) 

----Create Balance Sheet of Closing F Y 
EXEC T_spRptBalanceSheet @CompanyID,@dividend,@cost_of_goods_sold ,@rowMaterials ,@finishedGoods ,@WorkProgress,@PrevFYID 

--Save Opening and closing balance of Closing F Y into another Table 
INSERT INTO T_FiscalYearWiseAccountOpBalance ( FYID, AccountID, OpeningBalance, ClosingBalance, CompanyID)
SELECT @PrevFYID , AccountID,OpeningBalance,CurrentBalance,CompanyID FROM T_Account WHERE CompanyID=@CompanyID

--Set Opening to 0 of Income & Expense
UPDATE T_Account SET OpeningBalance=0 , CurrentBalance = 0 WHERE AccountID IN (SELECT AccountID FROM VW_AccountWithClassification WHERE AccountType IN ('Income','Expense') AND CompanyID=@CompanyID)
--Set Opening to Closing of Accounts other than Income & Expense
UPDATE T_Account SET OpeningBalance=CurrentBalance  WHERE AccountID IN (SELECT AccountID FROM VW_AccountWithClassification WHERE AccountType NOT IN ('Income','Expense') AND CompanyID=@CompanyID)
---Set Opening of Retuned Earning to Profit/Loss for New F Y
Declare @ReturnEarningAcc INT;
SET @ReturnEarningAcc =ISNULL((SELECT COUNT(AccountID) FROM T_Account WHERE AccountTitle LIKE 'Profit and Loss Appropriation' AND CompanyID=@CompanyID),0);

IF @ReturnEarningAcc <>1 
BEGIN
Declare @str Varchar(100);
SET @str = 'There is no Profit and Loss Appropriation Account.'+ char(13) + 'Create an account Titled '+char(39)+'Profit and Loss Appropriation' + char(39)+' Under Directly '+ char(39)+'Capital'+ char(39)+' Head';
 RAISERROR (@str, 16, 1)
END

UPDATE T_Account SET OpeningBalance=@plBalance,CurrentBalance = @plBalance WHERE AccountTitle LIKE 'Profit and Loss Appropriation' AND CompanyID=@CompanyID;
-- 
UPDATE FiscalYear SET enddate= @date ,UserID=@UserID, modifiedDate=getdate() WHERE FiscalYearID=@PrevFYID ;
INSERT INTO FiscalYear( Title, startdate, enddate, companyID,UserID, modifiedDate)
VALUES     (@newFYTitle,@date+1,NULL,@CompanyID,@UserID,getdate())
END
ELSE
BEGIN
INSERT INTO FiscalYear( Title, startdate, enddate, companyID,UserID, modifiedDate)
VALUES     (@newFYTitle,@date,NULL,@CompanyID,@UserID,getdate())
END
END







