CREATE PROCEDURE [dbo].[T_spRptProfitLossAccount]
	@stockRaw Money,
	@stockFinish Money,
	@WorkinProgress Money,
	@CompanyID INT,
	@FYID INT
AS
BEGIN
Declare @eDate DateTime;

SET @eDate = (SELECT endDate FROM FiscalYear WHERE FiscalYearID=@FYID AND CompanyID=@CompanyID);

IF @eDate IS NULL
BEGIN 
	Declare @ExportSales Money,@SellingExpense Money,@TurnOver Money;
	Declare @CostOfGoodsSold Money,@GrossProfit Money ,@AdminOverhead Money,@FinancialExpense Money,@OtherIncome Money,@NetProfit Money;

SET @ExportSales =  -ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Income') AND (ISNULL(AccountSubType,'') LIKE 'Sales Account') AND (CompanyID = @CompanyID)),0);
SET @SellingExpense = ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Selling Expense') AND (CompanyID = @CompanyID)),0);

SET @TurnOver = @ExportSales - @SellingExpense;
SET @CostOfGoodsSold = (SELECT dbo.fnGetCostOfGoodsSold(@stockRaw,@stockFinish,@WorkinProgress,@CompanyID))
SET @GrossProfit = @TurnOver - @CostOfGoodsSold;
SET @AdminOverhead = ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Administrative Overhead') AND (CompanyID = @CompanyID)),0);
SET @FinancialExpense =  ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Financial Expense') AND (CompanyID = @CompanyID)),0);
SET @OtherIncome = -ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Income') AND (ISNULL(AccountSubType,'') NOT LIKE 'Sales Account') AND (CompanyID = @CompanyID)),0);
SET @NetProfit = @GrossProfit + @OtherIncome -@AdminOverhead - @FinancialExpense ;

DELETE FROM t_report_proftandloss WHERE CompanyID=@CompanyID AND FYID=@FYID;
INSERT INTO t_report_proftandloss (FYID,TurnOver,CostOfGoodsSold,GrossProfit,AdminOverhead,FinancialExpense,OtherIncome,NetProfit,CompanyID) 
VALUES(@FYID,@TurnOver,@CostOfGoodsSold,@GrossProfit,@AdminOverhead,@FinancialExpense,@OtherIncome,@NetProfit,@CompanyID) ;

SELECT @TurnOver as TurnOver,@CostOfGoodsSold AS CostOfGoodsSold,@GrossProfit AS GrossProfit,@AdminOverhead AS AdminOverhead ,@FinancialExpense AS FinancialExpense,@OtherIncome AS OtherIncome,@NetProfit AS NetProfit;

END
ELSE
SELECT TurnOver,CostOfGoodsSold,GrossProfit,AdminOverhead,FinancialExpense,OtherIncome,NetProfit FROM t_report_proftandloss WHERE CompanyID=@CompanyID AND FYID=@FYID ;
END





