CREATE PROCEDURE [dbo].[T_SpRptProfitLossAppropreciation]
@stockRaw Money,
@stockFinish Money,
@Dividend Money,
@CompanyID INT,
@WorkinProgress Money,
@FYID INT	
AS
BEGIN

Declare @eDate DateTime;

SET @eDate = (SELECT endDate FROM FiscalYear WHERE FiscalYearID=@FYID AND CompanyID=@CompanyID);

IF @eDate IS NULL
BEGIN 
Declare @previous_capital money,@adding money;
Declare @ExportSales Money,@SellingExpense Money,@cuDepreciation Money;
Declare @CostOfGoodsSold Money,@GrossProfit Money ,@AdminOverhead Money,@FinancialExpense Money,@OtherIncome Money,@NetProfit Money;

SET @ExportSales =  -ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Income') AND (ISNULL(AccountSubType,'') LIKE 'Sales Account') AND (CompanyID = @CompanyID)),0);
SET @SellingExpense = ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Selling Expense') AND (CompanyID = @CompanyID)),0);
SET @CostOfGoodsSold = (SELECT dbo.fnGetCostOfGoodsSold(@stockRaw,@stockFinish,@WorkinProgress,@CompanyID))
SET @cuDepreciation =ISNULL((SELECT CurrentBalance FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (AccountTitle  LIKE 'Depreciation') AND (CompanyID = @CompanyID)),0);
SET @AdminOverhead = ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Administrative Overhead') AND (CompanyID = @CompanyID)),0);
SET @FinancialExpense =  ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Financial Expense') AND (CompanyID = @CompanyID)),0);
SET @OtherIncome = -ISNULL((SELECT SUM(CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Income') AND (ISNULL(AccountSubType,'') NOT LIKE 'Sales Account') AND (CompanyID = @CompanyID)),0);

SET @previous_capital =ISNULL((SELECT CurrentBalance FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Capital') AND (AccountTitle  LIKE 'Profit and Loss Appropriation') AND (CompanyID = @CompanyID)),0);
SET @adding = @ExportSales - @SellingExpense - @CostOfGoodsSold - @AdminOverhead - @FinancialExpense - @cuDepreciation + @OtherIncome;

DELETE FROM t_report_profitAndLoassA WHERE CompanyID=@CompanyID AND FYID=@FYID;

INSERT INTO t_report_profitAndLoassA (FYID,isBold,Title,Value,CompanyID)VALUES(@FYID,0,'Profit brought from Previous',@previous_capital,@CompanyID);
INSERT INTO t_report_profitAndLoassA (FYID,isBold,Title,Value,CompanyID)VALUES(@FYID,0,'Add During this year',@adding,@CompanyID);
INSERT INTO t_report_profitAndLoassA (FYID,isBold,Title,Value,CompanyID)VALUES(@FYID,1,'',@adding + @previous_capital,@CompanyID);
INSERT INTO t_report_profitAndLoassA (FYID,isBold,Title,Value,CompanyID)VALUES(@FYID,0,'Dividened Paid',@Dividend,@CompanyID);
INSERT INTO t_report_profitAndLoassA (FYID,isBold,Title,Value,CompanyID)VALUES(@FYID,1,'Balance Transfer to Balance Sheet',@adding + @previous_capital - @Dividend,@CompanyID);

SELECT 0 AS isBold,'Profit brought from Previous' AS Title,@previous_capital AS Value
UNION ALL
SELECT 0 AS isBold,'Add During this year'AS Title,@adding AS Value
UNION ALL
SELECT 1 AS isBold,''AS Title,@adding + @previous_capital AS Value
UNION ALL
SELECT 0 AS isBold,'Dividened Paid' AS Title, @Dividend AS Value
UNION ALL
SELECT 1 AS isBold,'Balance Transfer to Balance Sheet' AS Title, @adding + @previous_capital - @Dividend AS Value

END
ELSE

SELECT isBold,Title,Value FROM t_report_profitAndLoassA WHERE CompanyID=@CompanyID AND FYID=@FYID;
END






