                      dbo.fnCustomerSalesQty(@endDate, @endDate, @CompanyID) AS fnCustomerSalesQty_Today ON 
                      VW_Customers.CustomerID = fnCustomerSalesQty_Today.CustomerID LEFT OUTER JOIN
                      /*dbo.fnAccountBalance(@endDate - 1, @CompanyID)*/
					 (
					SELECT CustomerID, CompanyID,SUM(amt) Balance FROM
					(SELECT     CustomerID, CompanyID, OpeningAmount amt
					FROM         T_Opening_Stock
					UNION ALL
					SELECT     T_Stock_InOut_Master.CustSupplID CustomerID , T_Stock_InOut_Master.CompanyID, T_Stock_InOut_Detail.OutAmount amt
