AS
BEGIN
Declare @raw Money,@finish Money;

SET @raw= (SELECT dbo.fnGetStockOfRawFinish('Raw Material',@CompanyID));
SET @finish= (SELECT dbo.fnGetStockOfRawFinish('Finished Good',@CompanyID));

UPDATE    T_Account
SET  CurrentBalance =@raw
WHERE     (CompanyID = @CompanyID) AND (AccountTitle LIKE 'Stock of Raw Materials');

UPDATE    T_Account
SET  CurrentBalance =@finish
WHERE     (CompanyID = @CompanyID) AND (AccountTitle LIKE 'Stock of Finished Goods');
END





GO

