CREATE FUNCTION [dbo].[AmountOnCalculationRule]
(
    @EmployeeID as INT,
	@StartDate as DateTime,
	@EndDate AS DateTime,
	@CompanyID AS INT,
	@Rate AS Money,
	@RuleID AS INT
)
RETURNS Money
AS
BEGIN
  Declare @amount AS Money       
     
     -------This function calculate amount on Rate and Calculation rule ID
     -------Specification of this rule ID is defined in the table Rule Defined
           
IF @RuleID = 15
    SET @amount =(SELECT dbo.Calculation_OneDay_Gross(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
ELSE IF @RuleID = 16
     SET @amount =(SELECT dbo.Calculation_OneDay_Basic(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
ELSE IF @RuleID = 17
  BEGIN
     SET @amount =(SELECT dbo.Calculation_Basic(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
	 SET  @Rate = @Rate / 100;
  END
 ELSE IF @RuleID = 18
  BEGIN
     SET @amount =(SELECT dbo.Calculation_Gross(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
     SET  @Rate = @Rate / 100;
  END
 ELSE IF @RuleID = 19
  BEGIN
     SET @amount =(SELECT dbo.Calculation_Basic(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
     SET  @Rate =1
  END
                   
 ELSE IF @RuleID = 19
  BEGIN
     SET @amount =(SELECT dbo.Calculation_Gross(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
     SET  @Rate =1
  END   
			   SET @Amount = @Amount* @Rate
	
	RETURN ISNULL(@Amount,0)
END




GO

