	@CompanyID AS INT,
	@Rate AS Money,
	@RuleID AS INT
)
RETURNS Money
AS
BEGIN
  declare @Amount Money,  @Salary MONEY, @TTLDays INT ,@TTLWorkingDays INT     
     
     -------This function calculate amount on Rate and Calculation rule ID
     -------Specification of this rule ID is defined in the table Rule Defined
           
IF @RuleID = 16
Begin
	--Basic/Total Days Of the Month
    	SET @TTLDays = DateDiff(day,@startDate, @endDate)+1
		SET @Salary =  (SELECT dbo.F_Basic_Salary(@employeeID,@startDate, @endDate,@CompanyID))
		SET @Amount = @Salary/@TTLDays
		
END	
ELSE IF @RuleID = 16
Begin
--Basic/ Total Working days of the Month
	 SET @TTLWorkingDays = (SELECT dbo.TTL_Working_Days(@employeeID,@startDate, @endDate,@CompanyID)) 
	 SET @Salary =    (SELECT dbo.F_Basic_Salary(@employeeID,@startDate, @endDate,@CompanyID))
     SET @Amount = @Salary/@TTLWorkingDays
     
END	
IF @RuleID = 15
Begin
	--Gross/Total Days Of the Month
    	SET @TTLDays = DateDiff(day,@startDate, @endDate)+1
		SET @Salary =  (SELECT dbo.F_Gross_Salary(@employeeID,@startDate, @endDate,@CompanyID))
		SET @Amount = @Salary/@TTLDays
	
END	
ELSE IF @RuleID = 15
Begin
--Basic/ Total Working days of the Month
	 SET @TTLWorkingDays = (SELECT dbo.TTL_Working_Days(@employeeID,@startDate, @endDate,@CompanyID)) 
	 SET @Salary =    (SELECT dbo.F_Gross_Salary(@employeeID,@startDate, @endDate,@CompanyID))
     SET @Amount = @Salary/@TTLWorkingDays
     
END	
ELSE IF @RuleID = 17
  BEGIN
     SET @amount =(SELECT dbo.F_Basic_Salary(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
	 SET  @Rate = @Rate / 100;
  END
 ELSE IF @RuleID = 18
  BEGIN
     SET @amount =(SELECT dbo.F_Gross_Salary(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
     SET  @Rate = @Rate / 100;
  END
 ELSE IF @RuleID = 19
  BEGIN
     SET @amount =(SELECT dbo.F_Basic_Salary(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
     SET  @Rate =1
  END
                   
 ELSE IF @RuleID = 20
  BEGIN
     SET @amount =(SELECT dbo.F_Gross_Salary(	@EmployeeID,@StartDate,@EndDate, @CompanyID))
     SET  @Rate =1
  END   
			  
			   SET @Amount = @Amount* @Rate
	
