CREATE FUNCTION [dbo].[Calculation_OneDay_Gross]
	(
	
	@employeeID INT,
	@startDate DATETIME,
	@endDate DATETIME,
	@CompanyID INT
	)
RETURNS Money
AS
	BEGIN
	
	declare @Amount Money, @GrossSalary MONEY, @TTLDays INT ,@TTLWorkingDays INT,@RuleID INT
	 
	SET @RuleID = (SELECT Top 1 RuleDefined.RuleID FROM         RuleDefined INNER JOIN CompanyRule ON RuleDefined.RuleID = CompanyRule.RuleID
					WHERE     (RuleDefined.RuleGroup LIKE 'One Day Salary') AND CompanyID = @CompanyID)
		
	/*Gross/Total days of the month*/
	IF @RuleID =1
	BEGIN
		--Total Days of the Month
		SET @TTLDays = DateDiff(day,@startDate, @endDate)+1
		SET @GrossSalary =  (SELECT dbo.F_Gross_Salary(@employeeID,@startDate, @endDate,@CompanyID))
		SET @Amount = @GrossSalary/@TTLDays
	END
	
	/*Gross/Total Working days of the month*/
	IF @RuleID =3
	BEGIN
	--Total Working Days of the Month
	    SET @TTLWorkingDays = (SELECT dbo.TTL_Working_Days(@employeeID,@startDate, @endDate,@CompanyID)) 
	    SET @GrossSalary =    (SELECT dbo.F_Gross_Salary(@employeeID,@startDate, @endDate,@CompanyID))
		SET @Amount = @GrossSalary/@TTLWorkingDays
	END
	
		RETURN ISNULL(@Amount,0)
	END





