		SET @BasicSalary =  (SELECT dbo.F_Basic_Salary(@employeeID,@startDate, @endDate,@CompanyID))
		SET @Amount = (@BasicSalary/@TTLDays) * @AbsentDays
	END

--SET @Amount  = (SELECT dbo.F_OneDay_Salary(@employeeID,@startDate,@endDate,@CompanyID)) * @AbsentDays


