CREATE FUNCTION [dbo].[F_Attendance_Bonus]
(
    @employeeID INT,
	@startDate DATETIME,
	@endDate DATETIME,
	@CompanyID INT
	
	)
RETURNS Money
AS
	
BEGIN
	declare @AttBonusAmount Money, @AttDays INT, @RuleID INT,
	        @LaunchAbsentDay INT, @LateDay INT, @ATT_Days INT, @TTLDays INT, @LeaveDays INT

SET @LaunchAbsentDay =0  
SET @LateDay = 0

IF EXISTS(SELECT TOP 1 RuleID FROM CompanyRule WHERE CompanyID = @CompanyID AND RuleID = 8)
BEGIN
		SET @LaunchAbsentDay = (SELECT dbo.F_Lunch_Absent_Days(@employeeID,@startDate,@endDate,@CompanyID))
END
ELSE IF EXISTS(SELECT TOP 1 RuleID FROM CompanyRule WHERE CompanyID = @CompanyID AND RuleID = 7)
BEGIN 
		SET @LateDay = (SELECT dbo.F_LatePresent_Days(@employeeID,@startDate,@endDate,@CompanyID))
END	

SET @LeaveDays = ISNULL((SELECT dbo.F_Leave_Days(@employeeID,@startDate,@endDate,@CompanyID)),0)


SET @TTLDays = (DateDiff(day,@startDate, @endDate)+1)

SET @ATT_Days = (SELECT dbo.F_Att_Pay_Days(@employeeID,@startDate,@endDate,@CompanyID))

IF @ATT_Days < @TTLDays
	SET @AttBonusAmount = 0			
ELSE IF @LateDay > 0 OR @LaunchAbsentDay > 0 OR @LeaveDays >0
	SET @AttBonusAmount = 0		
ELSE
BEGIN		
set @AttBonusAmount= (SELECT TOP 1 AttendanceBonus.FixedAmount + dbo.AmountOnCalculationRule(@EmployeeID, @StartDate, @EndDate,  @CompanyID, AttendanceBonus.Rate, AttendanceBonus.RuleID) AS Amount FROM AttendanceBonus  WHERE CompanyID = @CompanyID AND EmployeeID =@EmployeeID)
 
END	



RETURN ISNULL(@AttBonusAmount,0)
END






