CREATE FUNCTION [dbo].[F_National_Holidays]
	(
	@employeeID INT,
	@startDate DATETIME,
	@endDate DATETIME,
	@CompanyID INT
	
	)
RETURNS INT
AS
	BEGIN

/* if holidays before joiningdate is not countable, Then  the code bellow is applicable*/
   DECLARE @JoinDate DATETIME;
   SET 	@JoinDate=(SELECT  TOP 1  JoiningDate FROM Employee WHERE EmployeeID=@EmployeeID AND CompanyID=@CompanyID);
   IF 	@JoinDate>@startDate
   SET @startDate= @JoinDate;
/* end */

/* if holidays after Release Date is not countable, Then  the code bellow is applicable*/
	DECLARE @releaseDate DATETIME;
    SET @releaseDate =(SELECT  TOP 1 effectiveDate FROM EmployeeHistory WHERE EmployeeID=@EmployeeID AND CompanyID=@CompanyID AND EmpStatusID NOT IN( 8 /*Active*/));
   IF @releaseDate<@endDate
    SET @endDate=@releaseDate;
/* end */

DECLARE @Holidays INT;
SET @Holidays=(SELECT  TOP 1 count(holidaydate) FROM HoliDays 
				WHERE (EmployeeID=@EmployeeID) 
				AND (holidaydate BETWEEN @startDate AND @endDate) 
				AND (CompanyID=@CompanyID) AND   (Remarks LIKE '0')
/* if before or after holiday is not present ,then that holiday is not countable. In this case the condition below is applicable */
				--AND ( holidaydate+1 IN (SELECT attDate FROM attendance WHERE EmployeeID =@EmployeeID) OR holidaydate-1 IN (SELECT attDate FROM attendance WHERE EmployeeID =@EmployeeID))
				
			   )
     	
    	
	RETURN @Holidays;
	END





