CREATE FUNCTION [dbo].[F_OT_HourExtra_Round]
	(
    @employeeID INT,
	@startDate DATETIME,
	@endDate DATETIME,
	@CompanyID INT
	)
RETURNS Money
AS
	
BEGIN
declare @OTHourBuyer Money, @OTHourActual Money,@xtraOT Money
Declare @MaxOT as INT

SET @MaxOT = isnull((SELECT top 1 MaxOverTime FROM SalarySheets WHERE IsDefault = 1 AND CompanyID = @CompanyID),0)
SET @xtraOT= (SELECT SUM(CASE WHEN xtraOT > ISNULL(MaxExtraOTHour.MaxExtraOT, 0) THEN ISNULL(MaxExtraOTHour.MaxExtraOT, 0) ELSE xtraOT END) AS xtra
				FROM 
				(SELECT attDate, EmployeeID, (CASE WHEN ISNULL(OThour, 0) > @MaxOT THEN ISNULL(OThour, 0) - @MaxOT ELSE 0 END) AS xtraOT, CompanyID
                       FROM          vw_AttInOut
                       WHERE      (EmployeeID = @EmployeeID) AND (CompanyID = @CompanyID) AND (OThour > 0) AND (attDate BETWEEN @startDate AND @endDate)) 
                      AS A LEFT OUTER JOIN
                      MaxExtraOTHour ON A.attDate = MaxExtraOTHour.OTDate AND A.CompanyID = MaxExtraOTHour.CompanyID)

RETURN ROUND(ISNULL(@xtraOT, 0),0)
END




