CREATE FUNCTION [dbo].[fn_LastDayOfMonth]
	(
	@dt DateTime
	)
RETURNS DateTime
AS

	BEGIN
	declare  @rdt DateTime,@adt DateTime;
	declare  @lastdate DateTime;
	
	set @rdt=dbo.Date(year(@dt),month(@dt),28)
	if MONTH(@rdt)=MONTH(DATEADD(DAY,1,@rdt) )+1
	   set @lastdate= @rdt
    else
		BEGIN 
			 set @rdt=dbo.Date(year(@dt),month(@dt),29)
			 if MONTH(@rdt)=MONTH(DATEADD(DAY,1,@rdt) )+1
			  set @lastdate= @rdt
			else
				BEGIN
					set @rdt=dbo.Date(year(@dt),month(@dt),30)
					if MONTH(@rdt)=MONTH(DATEADD(DAY,1,@rdt) )+1
					   set @lastdate= @rdt
					else
					   set @lastdate=dbo.Date(year(@dt),month(@dt),31)
				END 
		END
	
	RETURN @lastdate
	END





