CREATE FUNCTION [dbo].[fnGetProfitOrLoss]
(
	@cost_of_goods_sold Money,
	@dividend Money,
	@CompanyID INT
)
RETURNS Money
AS
BEGIN
DEclare @plBalance money;	

Declare @ExportSales Money,@OtherIncome Money,@AdminOverhead Money,@FinancialExpense Money,@BloomingDell Money;
Declare @previous_capital Money,@cuDepreciation Money;


SET @ExportSales =  -ISNULL((SELECT SUM(Nature*CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Income') AND (ISNULL(AccountSubType,'') LIKE 'Sales Account') AND (CompanyID = @CompanyID)),0);

SET @OtherIncome = -ISNULL((SELECT SUM(Nature*CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Income') /*AND AccountTitle NOT LIKE 'Profit and Loss Appropriation'*/ AND (ISNULL(AccountSubType,'') NOT LIKE 'Sales Account') AND (CompanyID = @CompanyID)),0);

SET @AdminOverhead = ISNULL((SELECT SUM(Nature*CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Administrative Overhead') AND (CompanyID = @CompanyID)),0);

SET @FinancialExpense =  ISNULL((SELECT SUM(Nature*CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Financial Expense') AND (CompanyID = @CompanyID)),0);
						
SET @BloomingDell = ISNULL((SELECT SUM(Nature*CurrentBalance) FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (ISNULL(AccountSubType,'')  LIKE 'Selling Expense') AND (CompanyID = @CompanyID)),0);
SET @previous_capital =-ISNULL((SELECT Nature*CurrentBalance FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Capital') AND (AccountTitle  LIKE 'Profit and Loss Appropriation') AND (CompanyID = @CompanyID)),0);
SET @cuDepreciation =ISNULL((SELECT Nature*CurrentBalance FROM  VW_AccountWithClassification WHERE (AccountType LIKE 'Expense') AND (AccountTitle  LIKE 'Depreciation') AND (CompanyID = @CompanyID)),0);

--plBalance = 
/*(
#(
(Me.ExportSales - Me.BloomingDell) 
select dbo.fnGetCostOfGoodsSold(0,2000,0,6)
- select dbo.fnGetProfitOrLoss(2000,0,6)
frmCostOfGoodSold.cost_of_goods_sold
#) 
- 
(Me.AdminOverhead + Me.FinancialExpense + cuDepreciation) 
+ 
Me.OtherIncome
*) 
+ 
Me.previous_capital 
- 
dividend*/

SET @plBalance = @ExportSales - @BloomingDell - @cost_of_goods_sold - @AdminOverhead-@FinancialExpense- @cuDepreciation + @OtherIncome + @previous_capital - @dividend ;

return ISNULL(@plBalance,0.0);
END











