CREATE FUNCTION [dbo].[OneDay_Basic]
	(
	@employeeID INT,
	@startDate DATETIME,
	@endDate DATETIME,
	@CompanyID INT
	--,@RuleID INT
	)
RETURNS Money
AS
	BEGIN
	
	declare @Amount Money,  @BasicSalary MONEY, @TTLDays INT ,@TTLWorkingDays INT,@RuleID INT
	 
	
	SET @RuleID = (SELECT  TOP 1  RuleDefined.RuleID FROM         RuleDefined INNER JOIN CompanyRule ON RuleDefined.RuleID = CompanyRule.RuleID
					WHERE     (RuleDefined.RuleGroup LIKE 'One Day Salary') AND CompanyID = @CompanyID) 
	
	
	
	/*Basic/Total days of the month*/
	IF @RuleID =2
	BEGIN
		--Total Days of the Month
		SET @TTLDays = DateDiff(day,@startDate, @endDate)+1
		SET @BasicSalary =  (SELECT dbo.F_Basic_Salary(@employeeID,@startDate, @endDate,@CompanyID))
		SET @Amount = @BasicSalary/@TTLDays
	END
	
	/*Basic/Total Working days of the month*/
	IF @RuleID =4
	BEGIN
	--Total Working Days of the Month
	    SET @TTLWorkingDays = (SELECT dbo.TTL_Working_Days(@employeeID,@startDate, @endDate,@CompanyID)) 
	    SET @BasicSalary =    (SELECT dbo.F_Basic_Salary(@employeeID,@startDate, @endDate,@CompanyID))
		SET @Amount = @BasicSalary/@TTLWorkingDays
	END
	
		RETURN ISNULL(@Amount,0)
	END





