CREATE FUNCTION [dbo].[TTL_Working_Days]
	(
	@employeeID INT,
	@startDate DATETIME,
	@endDate DATETIME,
	@CompanyID INT
	)
RETURNS Money
AS
	BEGIN
	
	declare @RuleID INT , @TTLWorkingDays INT, @TTLDays INT,@Holiday INT
	
	SET @RuleID = (SELECT  TOP 1  RuleDefined.RuleID FROM RuleDefined INNER JOIN CompanyRule ON RuleDefined.RuleID = CompanyRule.RuleID
					WHERE     (RuleDefined.RuleGroup LIKE 'Working Day Count') AND CompanyID = @CompanyID) 
	
	SET @TTLDays = (DateDiff(day,@startDate, @endDate)+1)
	
/*	
	-- Working days without weekly holidays
	IF @RuleID = 11
		BEGIN
			SET @TTLWorkingDays = @TTLDays - (SELECT dbo.F_Weekly_HoliDays(@employeeID,@startDate, @endDate, @CompanyID))
		END
	
	
	
	-- Working days without weekly and National holidays
	IF @RuleID = 12
		BEGIN
			SET @TTLWorkingDays = @TTLDays - (SELECT dbo.F_HoliDays(@employeeID,@startDate, @endDate, @CompanyID))
		END
	*/
	SET @Holiday=(SELECT COUNT(Holiday.HolidayDate) FROM EmployeeJobCalendar INNER JOIN Holiday ON EmployeeJobCalendar.JobCalenderID = Holiday.JobCalenderID 
			WHERE(EmployeeJobCalendar.EmployeeID = @EmployeeID) AND (Holiday.CompanyID = @CompanyID) AND (Holiday.HolidayDate BETWEEN @startDate AND @endDate));
	
	SET @TTLWorkingDays = @TTLDays - @Holiday;
	
			RETURN ISNULL(@TTLWorkingDays,0)
	END





