-- Triggers structure for table [dbo].[INVStockSerial]
-- ----------------------------
DROP TRIGGER [dbo].[trg_stockInsert]
GO
CREATE TRIGGER [dbo].[trg_stockInsert]
ON [dbo].[INVStockSerial]
AFTER INSERT
AS
	declare @newbagcount int;
	declare @rowid int;
	select @newbagcount=i.Bags from inserted i;	
	select @rowid=i.PartyID from inserted i;	

	update dbo.INVParty
         set INVParty.bagcount=bagcount+@newbagcount
				where INVParty.PartyID=@rowid;





GO

